<?php    in_file();    class Mdonate extends model    {        public $error = false, $vars = [];        public $hash_item = '';        protected $paypal_ipn_url = 'https://www.paypal.com/cgi-bin/webscr';		protected $paypal_ipn_url_ssl = 'www.paypal.com';												         protected $req = 'cmd=_notify-validate';        protected $post = [];        protected $paypal_response;        protected $paypal_debug = 0;        public $order_details = [];        protected $pw_reason_list = [0 => 'Invalid Reason', 1 => 'Chargeback', 2 => 'Credit Card fraud', 3 => 'Order fraud', 4 => 'Bad data entry', 5 => 'Fake / proxy user', 6 => 'Rejected by advertiser', 7 => 'Duplicate conversions', 8 => 'Goodwill credit taken back', 9 => 'Cancelled order', 10 => 'Partially reversed transaction'];        public $google_package = [];        public function __set($key, $val)        {            $this->vars[$key] = $val;        }        public function __isset($name)        {            return isset($this->vars[$name]);        }        public function set_ipn_listeners($data)        {            $server = explode('-', $data, 2)[1];            if($this->config->values('donation_config', [$server, 'paypal', 'sandbox']) == 1){                $this->paypal_ipn_url = 'https://www.sandbox.paypal.com/cgi-bin/webscr';                $this->paypal_ipn_url_ssl = 'www.sandbox.paypal.com';            }        }				public function findReferral($acc){			return $this->website->db('web')->query('SELECT refferer FROM DmN_Refferals WHERE refferal = \'' . $this->website->db('web')->sanitize_var($acc) . '\'')->fetch()['refferer'];		}        public function get_paypal_packages()        {            return $this->website->db('web')->query('SELECT id, package, reward, price, currency FROM DmN_Donate_Packages WHERE status = 1 AND server = \'' . $this->website->db('web')->sanitize_var($this->session->userdata(['user' => 'server'])) . '\' ORDER BY orders ASC')->fetch_all();        }        public function get_interkassa_packages()        {            return $this->website->db('web')->query('SELECT id, package, reward, price, currency FROM DmN_Donate_Interkassa_Packages WHERE status = 1 AND server = \'' . $this->website->db('web')->sanitize_var($this->session->userdata(['user' => 'server'])) . '\' ORDER BY orders ASC')->fetch_all();        }        public function get_paycall_packages()        {            return $this->website->db('web')->query('SELECT id, package, reward, price FROM DmN_Donate_PayCall_Packages WHERE status = 1 AND server = \'' . $this->website->db('web')->sanitize_var($this->session->userdata(['user' => 'server'])) . '\' ORDER BY orders ASC')->fetch_all();        }        public function get_cuenta_digital_packages()        {            return $this->website->db('web')->query('SELECT id, package, reward, price, currency FROM DmN_Donate_CuentaDigital_Packages WHERE status = 1 AND server = \'' . $this->website->db('web')->sanitize_var($this->session->userdata(['user' => 'server'])) . '\' ORDER BY orders ASC')->fetch_all();        }        public function check_package($id)        {            $count = $this->website->db('web')->snumrows('SELECT COUNT(id) as count FROM DmN_Donate_Packages WHERE id = ' . $this->website->db('web')->sanitize_var($id) . ' AND status = 1');            return ($count == 1);        }        public function get_paypal_package_data_by_id($id)        {            $stmt = $this->website->db('web')->prepare('SELECT TOP 1 reward, price, currency FROM DmN_Donate_Packages WHERE id = :id AND status = 1 AND server = :server');            $stmt->execute([':id' => $id, ':server' => $this->session->userdata(['user' => 'server'])]);            return $stmt->fetch();        }        public function get_paycall_package_data_by_id($id)        {            $stmt = $this->website->db('web')->prepare('SELECT TOP 1 reward, price FROM DmN_Donate_PayCall_Packages WHERE id = :id AND status = 1 AND server = :server');            $stmt->execute([':id' => $id, ':server' => $this->session->userdata(['user' => 'server'])]);            return $stmt->fetch();        }        public function get_cuenta_digital_package_data_by_id($id)        {            $stmt = $this->website->db('web')->prepare('SELECT TOP 1 reward, price, currency FROM DmN_Donate_CuentaDigital_Packages WHERE id = :id AND status = 1 AND server = :server');            $stmt->execute([':id' => $id, ':server' => $this->session->userdata(['user' => 'server'])]);            return $stmt->fetch();        }        public function get_interkassa_package_data_by_id($id)        {            $stmt = $this->website->db('web')->prepare('SELECT TOP 1 reward, price, currency FROM DmN_Donate_Interkassa_Packages WHERE id = :id AND status = 1 AND server = :server');            $stmt->execute([':id' => $id, ':server' => $this->session->userdata(['user' => 'server'])]);            return $stmt->fetch();        }        public function insert_paypal_order($reward, $price, $currency, $isBattlePass = 0, $isKeys = 0)        {            $this->hash_item = md5($this->session->userdata(['user' => 'username']) . $price . $currency . uniqid(microtime(), 1));            if($isBattlePass == 1){                $this->hash_item = 'bp-'.$this->hash_item;            }            if($isKeys == 1){                $this->hash_item = 'mk-'.$this->hash_item;            }            $stmt = $this->website->db('web')->prepare('INSERT INTO DmN_Donate_Orders (amount, currency, credits, account, server, hash) VALUES(:amount, :currency, :credits, :account, :server, :hash)');            return $stmt->execute([':amount' => $price, ':currency' => $currency, ':credits' => $reward, ':account' => $this->session->userdata(['user' => 'username']), ':server' => $this->session->userdata(['user' => 'server']), ':hash' => $this->hash_item]);        }        public function insert_interkassa_order($reward, $price, $currency)        {            $this->hash_item = md5($this->session->userdata(['user' => 'username']) . $price . $currency . uniqid(microtime(), 1));            $stmt = $this->website->db('web')->prepare('INSERT INTO DmN_Donate_Interkassa_Orders (amount, currency, credits, account, server, hash) VALUES(:amount, :currency, :credits, :account, :server, :hash)');            return $stmt->execute([':amount' => $price, ':currency' => $currency, ':credits' => $reward, ':account' => $this->session->userdata(['user' => 'username']), ':server' => $this->session->userdata(['user' => 'server']), ':hash' => $this->hash_item]);        }        public function insert_paycall_order($reward, $price)        {            $this->hash_item = md5($this->session->userdata(['user' => 'username']) . $price . uniqid(microtime(), 1));            $stmt = $this->website->db('web')->prepare('INSERT INTO DmN_Donate_Paycall_Orders (amount, credits, account, server, hash) VALUES(:amount, :credits, :account, :server, :hash)');            return $stmt->execute([':amount' => $price, ':credits' => $reward, ':account' => $this->session->userdata(['user' => 'username']), ':server' => $this->session->userdata(['user' => 'server']), ':hash' => $this->hash_item]);        }        public function get_paypal_data()        {            return ['email' => $this->config->values('donation_config', [$this->session->userdata(['user' => 'server']), 'paypal', 'email']), 'item' => $this->hash_item, 'user' => $this->session->userdata(['user' => 'username']), 'server' => $this->session->userdata(['user' => 'server'])];        }        public function get_paycall_data($reward, $price)        {            return ['business_code' => $this->config->values('donation_config', [$this->session->userdata(['user' => 'server']), 'paycall', 'business_code']), 'item' => $this->hash_item, 'user' => $this->session->userdata(['user' => 'username']), 'server' => $this->session->userdata(['user' => 'server']), 'reward' => $reward, 'price' => $price, 'business_name' => $this->config->config_entry('main|servername')];        }        public function check_paycall_order($item)        {            $stmt = $this->website->db('web')->prepare('SELECT TOP 1 amount, credits, account, server FROM DmN_Donate_PayCall_Orders WHERE hash = :item');            $stmt->execute([':item' => $item]);            $this->order_details = $stmt->fetch();        }        public function verifiedTransaction($paycall_unique, $business_code, $test_mode = 0)        {            $requestArg = ["service" => "chckTrans", "tId" => $paycall_unique, "cId" => $business_code, "test" => ($test_mode == 1) ? 1 : 0];            $res = $this->get_paycall_url('http://admin.paycall.co.il/ws.php', 0, http_build_query($requestArg));            $info = simplexml_load_string($res);            if((string)$info->RESULT == "true" && (string)$info->LOOKUP == "0")                return true;            return false;        }        private function get_paycall_url($url, $wsMethod = 1, $argQuery = "")        {            if($wsMethod == 1){                if(strpos($url, "?") !== false)                    $url = $url . "&" . $argQuery; else                    $url = $url . "?" . $argQuery;            }            if(function_exists('curl_init') == true){                $ch = curl_init();// || $this->writelog(curl_error(), 'Paycall');                curl_setopt($ch, CURLOPT_URL, $url);                if($wsMethod == 0){                    curl_setopt($ch, CURLOPT_POST, 1);                    curl_setopt($ch, CURLOPT_POSTFIELDS, $argQuery);                }                curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);                $data = curl_exec($ch);                curl_close($ch);            } else{                if($wsMethod == 1){                    $file = fopen($url, "r");                    if(!$file){                        $this->writelog('Unable to open remote file.', 'Paycall');                        exit;                    }                    while(!feof($file))                        $data .= fgets($file, 64);                    fclose($file);                } else{                    $opts = ['http' => ['method' => 'POST', 'header' => 'Content-type: application/x-www-form-urlencoded', 'content' => $argQuery]];                    $context = stream_context_create($opts);                    $file = fopen($url, "r", false, $context);                    if(!$file){                        $this->writelog('Unable to open remote file.', 'Paycall');                        exit;                    }                    $data = '';                    while(!feof($file))                        $data .= fgets($file, 64);                    fclose($file);                }            }            return $data;        }        public function get_2checkout_packages()        {            return $this->website->db('web')->query('SELECT id, package, reward, price, currency FROM DmN_2CheckOut_Packages WHERE status = 1 AND server = \'' . $this->website->db('web')->sanitize_var($this->session->userdata(['user' => 'server'])) . '\' ORDER BY orders ASC')->fetch_all();        }        public function get_pagseguro_packages()        {            return $this->website->db('web')->query('SELECT id, package, reward, price, currency FROM DmN_PagSeguro_Packages WHERE status = 1 AND server = \'' . $this->website->db('web')->sanitize_var($this->session->userdata(['user' => 'server'])) . '\' ORDER BY orders ASC')->fetch_all();        }        public function check_2checkout_package($id = -1)        {            $stmt = $this->website->db('web')->prepare('SELECT package, reward, price, currency FROM DmN_2CheckOut_Packages WHERE status = 1 AND id = :id');            $stmt->execute([':id' => $this->website->c($id)]);            return $stmt->fetch();        }        public function check_pagseguro_package($id = -1)        {   