<?php    if(!function_exists('utf8_uri_encode')){        function utf8_uri_encode($utf8_string, $length = 0)        {            $unicode = '';            $values = [];            $num_octets = 1;            $unicode_length = 0;            $string_length = strlen($utf8_string);            for($i = 0; $i < $string_length; $i++){                $value = ord($utf8_string[$i]);                if($value < 128){                    if($length && ($unicode_length >= $length))                        break;                    $unicode .= chr($value);                    $unicode_length++;                } else{                    if(count($values) == 0)                        $num_octets = ($value < 224) ? 2 : 3;                    $values[] = $value;                    if($length && ($unicode_length + ($num_octets * 3)) > $length)                        break;                    if(count($values) == $num_octets){                        if($num_octets == 3){                            $unicode .= '%' . dechex($values[0]) . '%' . dechex($values[1]) . '%' . dechex($values[2]);                            $unicode_length += 9;                        } else{                            $unicode .= '%' . dechex($values[0]) . '%' . dechex($values[1]);                            $unicode_length += 6;                        }                        $values = [];                        $num_octets = 1;                    }                }            }            return $unicode;        }    }    if(!function_exists('_')){        function _($text)        {            return __($text);        }    }    if(!function_exists('seems_utf8')){        function seems_utf8($str)        {            $length = strlen($str);            for($i = 0; $i < $length; $i++){                $c = ord($str[$i]);                if($c < 0x80)                    $n = 0; else if(($c & 0xE0) == 0xC0)                    $n = 1;                else if(($c & 0xF0) == 0xE0)                    $n = 2;                else if(($c & 0xF8) == 0xF0)                    $n = 3;                else if(($c & 0xFC) == 0xF8)                    $n = 4;                else if(($c & 0xFE) == 0xFC)                    $n = 5;                else return false;                for($j = 0; $j < $n; $j++){                    if((++$i == $length) || ((ord($str[$i]) & 0xC0) != 0x80))                        return false;                }            }            return true;        }    }    if(!function_exists('to_case')){        function to_case($str)        {            return strtoupper($str);        }    }    if(!function_exists('seo_string')){        function seo_string($title)        {            $title = strip_tags($title);            $title = preg_replace('|%([a-fA-F0-9][a-fA-F0-9])|', '---$1---', $title);            $title = str_replace('%', '', $title);            $title = preg_replace('|---([a-fA-F0-9][a-fA-F0-9])---|', '%$1', $title);            if(seems_utf8($title)){                if(function_exists('mb_strtolower')){                    $title = mb_strtolower($title, 'UTF-8');                }                $title = utf8_uri_encode($title, 200);            }            $title = strtolower($title);            $title = preg_replace('/&.+?;/', '', $title);            $title = str_replace('.', '-', $title);            $title = preg_replace('/[^%a-z0-9 _-]/', '', $title);            $title = preg_replace('/\s+/', '-', $title);            $title = preg_replace('|-+|', '-', $title);            $title = trim($title, '-');            return $title;        }    }    	function cmsVersion($msg, $password){		$password = sha1($password);		$iv	= '14a6fb80372bccd8';		$salt = hash('sha256', $password . '8f3fe3df793c886c37dfa016c8167ef2aaced1ac');		$encrypted_msg = $msg;		$decrypted_msg = openssl_decrypt($encrypted_msg, 'aes-256-cbc', $salt, null, $iv);		if($decrypted_msg === false)			return false;		return $decrypted_msg;	}        if(!function_exists('is_ajax')){        function is_ajax()        {            return isset($_POST['ajax']) || isset($_GET['ajax']);        }    }    if(!function_exists('json')){        function json($data = [])        {            header('Content-Type: application/json');            die(json_encode($data));        }    }    if(!function_exists('cms_version')){        function cms_version($str)        {            return base64_decode($str);        }    }    if(!function_exists('in_file')){        function in_file()        {            if(defined('DMNCMS')){                if(!DMNCMS)                    die('Your not allowed to access this file!');            } else{                die('Your not allowed to access this file!');            }            return true;        }    }    if (!function_exists('ip')) {		function ip($int = false)		{            $ip = '0.0.0.0';			if(!empty($_SERVER['HTTP_CLIENT_IP'])){				$ip = $_SERVER['HTTP_CLIENT_IP'];			}             if(!empty($_SERVER['HTTP_X_FORWARDED_FOR'])){				$ip = $_SERVER['HTTP_X_FORWARDED_FOR'];			}             if(!empty($_SERVER['REMOTE_ADDR'])) {				$ip = $_SERVER['REMOTE_ADDR'];			}             if(!empty($_SERVER['HTTP_CF_CONNECTING_IP'])){				$ip = $_SERVER['HTTP_CF_CONNECTING_IP'];            }             if(!empty($_SERVER['HTTP_X_REAL_IP'])){				$ip = $_SERVER['HTTP_X_REAL_IP'];               }			if(strpos($ip, ',') !== false) {				list($ip) = explode(',', $ip);			}			$test = str_replace(array('.', ',', ' '), '', $ip);			if(!ctype_digit($test)){				$ip = $_SERVER['REMOTE_ADDR'];			}			$ip = ip2long($ip);			if(!$int) {				$ip = long2ip($ip);			}			return $ip;		}	}    if(!function_exists('use_funcs')){        function use_funcs($a, $b, $c)        {            return str_repeat($c, 2) . to_case($b) . cms_version('MTEwU3RhYmxl');        }    }    if(!function_exists('check_ip_white_list')){        function check_ip_white_list($ip_list = [])        {            if(!is_array($ip_list)){                if(strstr($ip_list, ',') !== false){                    $ip_list = explode(',', $ip_list);                } else{                    $ip_list = [$ip_list];                }            }            if(!in_array(ip(), $ip_list)){                header("HTTP/1.1 403 Forbidden");                exit('Restricted Area');            }        }    }    if(!function_exists('pre')){        function pre($val, $return = false, $color = '#fff')        {            echo '<pre style="color: ' . $color . ';">';            print_r($val, $return);            echo '</pre>';        }    }    if(!function_exists('writelog')){        function writelog($logentry, $lgname)        {            $log = '[' . ip() . '] ' . $logentry . '';            $log_name = APP_PATH . DS . 'logs' . DS . $lgname . '_' . date("m-d-y") . '.txt';            $logfile = @fopen($log_name, "a+");            if($logfile){                fwrite($logfile, "[" . date("h:iA") . "] $log\r\n");                fclose($logfile);            }        }    }    if(!function_exists('server_list')){        // @ioncube.dk cmsVersion('g8LU2sewjnwUpNnBTm9t85c3Xgf/0Y9V+rZWvw94O3A=', '009869451363953188238779430856374927754') -> "NewDmNIonCubeDynKeySecurityAlgo" RANDOM        function server_list($serv = '', $check_multi_acc = false)        {            $file = file_get_contents(APP_PATH . DS . 'data' . DS . 'serverlist.json');            $servers = json_decode($file, true);            if(is_array($servers)){                if($check_multi_acc == true){                    return $servers['USE_MULTI_ACCOUNT_DB'];                } else{                    unset($servers['USE_MULTI_ACCOUNT_DB']);                    if($serv != ''){                        if(array_key_exists($serv, $servers)){                            return $servers[$serv];                        } else{                            return false;                        }                    }                    return $servers;                }            } else{                throw new Exception('Unable to load server list. Please check configuration file.');            }        }    }    if(!function_exists('get_country_code')){        // @ioncube.dk use_funcs("DmN ","cms", "DmN") -> "DmNDmNCMS110Stable"        function get_country_code($ip = '127.0.0.1')        {            if(filter_var($ip, FILTER_VALIDATE_IP) != false){                if($ip != '127.0.0.1'){                    return collect_country($ip);                }            }            return 'us';        }    }    if(!function_exists('collect_country')){        function collect_country($ip = '')        {            $insert = true;            $country_list = file_get_contents(APP_PATH . DS . 'data' . DS . 'country_list.txt');            if(empty($country_list)){                $country = find_country_code($ip);                $ip_data = [$ip => $country];            } else{                $ip_data = unserialize($country_list);                if(array_key_exists($ip, $ip_data)){                    $country = $ip_data[$ip];                    $insert = false;                } else{                    $country = find_country_code($ip);                    $ip_data[$ip] = $country;                }            }            if($insert){                file_put_contents(APP_PATH . DS . 'data' . DS . 'country_list.txt', serialize($ip_data));            }            return $country;        }    }    if(!function_exists('find_country_code')){        function find_country_code($ip)        {            static $geoData = null;            if($geoData == null)                $geoData = geoip_open(APP_PATH . DS . 'data' . DS . 'GeoIP.dat', GEOIP_STANDARD);			return strtolower(geoip_country_code_by_addr($geoData, $ip));        }    }	    function g($v, $w)    {        $x = $v . ' yes';        $x .= ' ' . str_repeat($w, 2);        $x .= ' ' . substr($w, 0, -2);        return $x;    }    if(!function_exists('findCountryCodeRemotely')){        function findCountryCodeRemotely($ip)        {            $opts = ['http' => ['header' => "User-Agent:Mozilla/5.0 (Windows NT 6.2) AppleWebKit/537.1 (KHTML, like Gecko) Chrome/21.0.1180.75 Safari/537.1\r\n", 'timeout' => 10]];            $context = stream_context_create($opts);            if(($get_ip_info = file_get_contents('http://www.geoplugin.net/json.gp?ip=' . $ip, false, $context)) != false){                $get_ip_info = json_decode($get_ip_info, true);                return isset($get_ip_info['geoplugin_countryCode']) ? strtolower($get_ip_info['geoplugin_countryCode']) : 'us';            }            return 'us';        }    }	    if(!function_exists('arithmetic')){        function arithmetic($equation)        {            $result = 0;            $equation = preg_replace("/[^a-z0-9+\-.*\/()%]/", "", $equation);            $equation = preg_replace("/([a-z])+/i", "\$$0", $equation);            $equation = preg_replace("/([+-])([0-9]{1})(%)/", "*(1\$1.0\$2)", $equation);            $equation = preg_replace("/([+-])([0-9]+)(%)/", "*(1\$1.\$2)", $equation);            $equation = preg_replace("/([0-9]{1})(%)/", ".0\$1", $equation);            $equation = preg_replace("/([0-9]+)(%)/", ".\$1", $equation);            if($equation != ""){                $result = @eval("return " . $equation . ";");            }            if($result == null){                return 0;            }            return $result;        }    }	    function getPHPExecutablePath()    {        if(strtolower(PHP_SHLIB_SUFFIX) === 'dll'){            $paths = explode(PATH_SEPARATOR, getenv('PATH'));            foreach($paths as $path){                if(strstr($path, 'php.exe') && isset($_SERVER["WINDIR"]) && file_exists($path) && is_file($path)){                    return $path;                } else{                    $php_executable = $path . DIRECTORY_SEPARATOR . "php" . (isset($_SERVER["WINDIR"]) ? ".exe" : "");                    if(file_exists($php_executable) && is_file($php_executable)){                        return $php_executable;                    }                }            }            return PHP_BINARY;        } else{            return PHP_BINDIR . DS . 'php';        }    }    function isCommandLineInterface()    {        return (php_sapi_name() === 'cli');    }    if(!function_exists('load_class')){        function load_class($class)        {            static $_classes = [];            if(isset($_classes[$class])){                return $_classes[$class];            }            $name = false;            $file = SYSTEM_PATH . DS . $class . '.class.php';			if($class == 'LicenseKey'){				$name = $class;			}			else{				if(file_exists($file)){					$name = $class;					if(class_exists($name) === false){						require($file);					}				}			}            if($name === false){                exit('Unable to locate the specified class: ' . $class . '.php');            }            is_loaded($class);            $_classes[$class] = new $name();            return $_classes[$class];        }    }    if(!function_exists('is_loaded')){        function is_loaded($class = '')        {            static $_is_loaded = [];            if($class != ''){                $_is_loaded[strtolower($class)] = $class;            }            return $_is_loaded;        }    }    if(!function_exists('array_save')){        function array_save($array, $file)        {            file_put_contents($file, json_encode($array), LOCK_EX);        }    }    if(!function_exists('array_load')){        function array_load($file)        {            static $array_list = [];            $filehash = crc32($file);            if(!isset($array_list[$filehash])){                $array_list[$filehash] = json_decode