   $temp = [];                    foreach($v as $v2){                        $temp[] = $v2;                    }                    $kv[] = $k . ' = ' . join('|', $temp);                else:                    $kv[] = $k . ' = ' . $v;                endif;            }            $post_vars = join('&', $kv);            writelog($post_vars, '$_GET');        }    }		if(!function_exists('print_exception')){        function print_exception($e)        {            ob_clean();            $message = '<p>' . implode('</p><p>', (!is_array($e->getMessage())) ? [$e->getMessage()] : $e->getMessage()) . '</p>';            $html = '<!DOCTYPE html>				<html lang="en">				<head>				<title>Error</title>				<style type="text/css">				::selection{ background-color: #E13300; color: white; }				::moz-selection{ background-color: #E13300; color: white; }				::webkit-selection{ background-color: #E13300; color: white; }				body {					background-color: #fff;					margin: 40px;					font: 13px/20px normal Helvetica, Arial, sans-serif;					color: #4F5155;				}				a {					color: #003399;					background-color: transparent;					font-weight: normal;				}				h1 {					color: #444;					background-color: transparent;					border-bottom: 1px solid #D0D0D0;					font-size: 19px;					font-weight: normal;					margin: 0 0 14px 0;					padding: 14px 15px 10px 15px;				}				code {					font-family: Consolas, Monaco, Courier New, Courier, monospace;					font-size: 12px;					background-color: #f9f9f9;					border: 1px solid #D0D0D0;					color: #002166;					display: block;					margin: 14px 0 14px 0;					padding: 12px 10px 12px 10px;				}				#container {					margin: 10px;					border: 1px solid #D0D0D0;					-webkit-box-shadow: 0 0 8px #D0D0D0;				}				p {					margin: 12px 15px 12px 15px;				}				</style>				</head>				<body>					<div id="container">						<h1>Error</h1>						' . $message . '					</div>				</body>				</html>';            echo $html;        }    }